import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Form from "./Components/Form";
import ItemsList from "./Components/ItemsList";
import "./App.css";
import { List } from "stream-chat-react/dist/components/AutoCompleteTextarea/List";

const App = () => {
  const items = [];
  const [itemsList, setItemsList] = useState(items);
  const clickHandler = (item) => {
    setItemsList((prevItems) => {
      return [item, ...prevItems];
    });
  };
  const deleteHandler = (taskId) => {
    const updatedList = itemsList.filter((item) => {
      return taskId !== item.id;
    });
    setItemsList(updatedList);
  };
  return (
    <div>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Form click={clickHandler} />} />
          <Route
            path="/itemList"
            element={<ItemsList items={itemsList} onDelete={deleteHandler} />}
          />
        </Routes>
      </BrowserRouter>
    </div>
  );
};

export default App;
