import { React, useState } from "react";
import { Button } from "@mui/material";
import "./form.css";
const Form = (props) => {
  const [item, setItem] = useState("Todo-List");
  const changeHandler = (e) => {
    const EnteredItem = e.target.value;
    setItem(EnteredItem);
  };
  return (
    <div className="form-div">
      <form className="form">
        <h1 className="form-h1">{item}</h1>
        <input onChange={changeHandler} className="input" type="text" />
        <Button
          onClick={clickhandler}
          className="btn"
          variant="contained"
          size="large"
        >
          Add Item
        </Button>
      </form>
    </div>
  );
};

export default Form;
